-module(bs01).-export([first_word/1]).first_word(BinText) -> first_word(BinText, <<>>).first_word(<<" ", _/binary>>, Acc)  -> Acc;first_word(<<S, Rest/binary>>, Acc) -> 	first_word(Rest, <<Acc/binary, S>>);first_word(<<>>, Acc)               -> Acc.-ifdef(TEST).-include_lib("eunit/include/eunit.hrl").first_word_test_() -> [	?_assert(first_word(<<"Some text">>) =:= <<"Some">>),	?_assert(first_word(<<"Some text text">>) =:= <<"Some">>)	].-endif.% BS01: Извлечь из строки первое слово:% Пример:% 1> BinText = <<"Some text">>.% <<"Some Text">>% 2> bs01:first_word(BinText).% <<"Some">>